import React, { useState, useEffect, useRef } from 'react';

const Magnet = ({ x, y, type, onDrag }) => {
  const magnetRef = useRef(null);

  const handleMouseDown = (e) => {
    e.preventDefault();
    const offsetX = e.clientX - x;
    const offsetY = e.clientY - y;

    const onMouseMove = (e) => {
      onDrag(e.clientX - offsetX, e.clientY - offsetY);
    };

    const onMouseUp = () => {
      window.removeEventListener('mousemove', onMouseMove);
      window.removeEventListener('mouseup', onMouseUp);
    };

    window.addEventListener('mousemove', onMouseMove);
    window.addEventListener('mouseup', onMouseUp);
  };

  const magnetStyle = {
    left: `${x}px`,
    top: `${y}px`,
    position: 'absolute',
    width: '60px',
    height: '60px',
    backgroundColor: type === 'north' ? '#ff5733' : '#3498db',
    borderRadius: '50%',
    cursor: 'pointer',
    boxShadow: '0 4px 12px rgba(0, 0, 0, 0.1)',
    transition: 'all 0.1s ease-in-out',
    zIndex: 10,
  };

  return (
    <div
      ref={magnetRef}
      className={`magnet ${type}`}
      style={magnetStyle}
      onMouseDown={handleMouseDown}
    />
  );
};

const App = () => {
  const [magnets, setMagnets] = useState([
    { id: 1, x: 100, y: 100, type: 'north' },
    { id: 2, x: 300, y: 200, type: 'south' },
  ]);

  const updateMagnetPosition = (id, x, y) => {
    setMagnets((prevMagnets) =>
      prevMagnets.map((magnet) =>
        magnet.id === id ? { ...magnet, x, y } : magnet
      )
    );
  };

  const checkInteraction = () => {
    // Check interaction between magnets, e.g., attraction/repulsion
    setMagnets((prevMagnets) => {
      return prevMagnets.map((magnet) => {
        return {
          ...magnet,
          // Apply simple distance-based interaction logic here (if needed)
        };
      });
    });
  };

  useEffect(() => {
    checkInteraction();
  }, [magnets]);

  const appStyle = {
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    height: '100vh',
    backgroundColor: '#f8f9fa',
    flexDirection: 'column',
    textAlign: 'center',
    fontFamily: `'Roboto', sans-serif`,
    padding: '20px',
  };

  const headerStyle = {
    marginBottom: '20px',
    fontSize: '2rem',
    fontWeight: 'bold',
    color: '#333',
    textShadow: '1px 1px 3px rgba(0, 0, 0, 0.1)',
  };

  const containerStyle = {
    position: 'relative',
    width: '80%',
    height: '70%',
    border: '2px dashed #ddd',
    borderRadius: '10px',
    backgroundColor: '#ffffff',
    boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)',
  };

  return (
    <div style={appStyle}>
      <h1 style={headerStyle}>Magnet Simulation</h1>
      <div style={containerStyle}>
        {magnets.map((magnet) => (
          <Magnet
            key={magnet.id}
            x={magnet.x}
            y={magnet.y}
            type={magnet.type}
            onDrag={(newX, newY) => updateMagnetPosition(magnet.id, newX, newY)}
          />
        ))}
      </div>
      <div style={{ marginTop: '20px', fontSize: '1rem', color: '#555' }}>
        Drag the magnets to see how they interact.
      </div>
    </div>
  );
};

export default App;
