import React, { useState, useEffect, useRef } from 'react';

// Magnet component
const Magnet = ({ x, y, type, onDrag, onRotate }) => {
  const magnetRef = useRef(null);

  const handleMouseDown = (e) => {
    e.preventDefault();
    const offsetX = e.clientX - x;
    const offsetY = e.clientY - y;

    const onMouseMove = (e) => {
      onDrag(e.clientX - offsetX, e.clientY - offsetY);
    };

    const onMouseUp = () => {
      window.removeEventListener('mousemove', onMouseMove);
      window.removeEventListener('mouseup', onMouseUp);
    };

    window.addEventListener('mousemove', onMouseMove);
    window.addEventListener('mouseup', onMouseUp);
  };

  const magnetStyle = {
    left: `${x}px`,
    top: `${y}px`,
    position: 'absolute',
    width: '70px',
    height: '70px',
    backgroundColor: type === 'north' ? '#ff2d55' : '#007aff',
    borderRadius: '50%',
    cursor: 'grab',
    boxShadow: '0 6px 15px rgba(0, 0, 0, 0.1)',
    transition: 'all 0.2s ease-in-out',
    zIndex: 10,
    transform: `rotate(${onRotate}deg)`,
  };

  return (
    <div
      ref={magnetRef}
      className={`magnet ${type}`}
      style={magnetStyle}
      onMouseDown={handleMouseDown}
    />
  );
};

// Main App component
const App = () => {
  const [magnets, setMagnets] = useState([
    { id: 1, x: 100, y: 100, type: 'north', rotation: 0 },
    { id: 2, x: 300, y: 200, type: 'south', rotation: 0 },
  ]);

  const [reset, setReset] = useState(false);

  const updateMagnetPosition = (id, x, y) => {
    setMagnets((prevMagnets) =>
      prevMagnets.map((magnet) =>
        magnet.id === id ? { ...magnet, x, y } : magnet
      )
    );
  };

  const checkInteraction = () => {
    setMagnets((prevMagnets) => {
      return prevMagnets.map((magnet) => {
        return {
          ...magnet,
          rotation: calculateRotation(magnet),
        };
      });
    });
  };

  const calculateRotation = (magnet) => {
    // Logic for rotating magnet based on interaction
    const otherMagnet = magnets.find((m) => m.id !== magnet.id);
    const dx = otherMagnet.x - magnet.x;
    const dy = otherMagnet.y - magnet.y;
    const angle = Math.atan2(dy, dx) * (180 / Math.PI);
    return angle;
  };

  useEffect(() => {
    checkInteraction();
  }, [magnets]);

  const resetPositions = () => {
    setMagnets([
      { id: 1, x: 100, y: 100, type: 'north', rotation: 0 },
      { id: 2, x: 300, y: 200, type: 'south', rotation: 0 },
    ]);
  };

  const appStyle = {
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    height: '100vh',
    backgroundColor: '#f2f2f2',
    flexDirection: 'column',
    fontFamily: "'Roboto', sans-serif",
    padding: '20px',
  };

  const headerStyle = {
    fontSize: '2.5rem',
    fontWeight: '700',
    color: '#333',
    textShadow: '2px 2px 8px rgba(0, 0, 0, 0.1)',
    marginBottom: '20px',
    letterSpacing: '0.5px',
    textTransform: 'uppercase',
  };

  const containerStyle = {
    position: 'relative',
    width: '80%',
    height: '70%',
    backgroundColor: '#fff',
    borderRadius: '12px',
    boxShadow: '0 8px 20px rgba(0, 0, 0, 0.1)',
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    border: 'none',
    transition: 'all 0.3s ease',
    overflow: 'hidden',
    transform: 'scale(1)',
  };

  const buttonStyle = {
    marginTop: '20px',
    padding: '10px 20px',
    fontSize: '1rem',
    backgroundColor: '#4CAF50',
    color: '#fff',
    border: 'none',
    borderRadius: '5px',
    cursor: 'pointer',
    boxShadow: '0 4px 10px rgba(0, 0, 0, 0.2)',
    transition: 'background-color 0.3s',
  };

  const infoTextStyle = {
    fontSize: '1.2rem',
    color: '#555',
    marginTop: '20px',
    textAlign: 'center',
    fontWeight: '600',
    letterSpacing: '1px',
  };

  return (
    <div style={appStyle}>
      <h1 style={headerStyle}>Magnet Simulation</h1>
      <div style={containerStyle}>
        {magnets.map((magnet) => (
          <Magnet
            key={magnet.id}
            x={magnet.x}
            y={magnet.y}
            type={magnet.type}
            onDrag={(newX, newY) => updateMagnetPosition(magnet.id, newX, newY)}
            onRotate={magnet.rotation}
          />
        ))}
      </div>
      <button style={buttonStyle} onClick={resetPositions}>Reset Magnets</button>
      <div style={infoTextStyle}>
        <p>Drag the magnets around to see how they interact.</p>
        <p>Red is North, Blue is South. Watch how they rotate and attract/repel.</p>
      </div>
    </div>
  );
};

export default App;
