import React, { useState, useEffect, useRef } from 'react';

const Magnet = ({ x, y, type, onDrag }) => {
  const magnetRef = useRef(null);

  const handleMouseDown = (e) => {
    e.preventDefault();
    const offsetX = e.clientX - x;
    const offsetY = e.clientY - y;

    const onMouseMove = (e) => {
      onDrag(e.clientX - offsetX, e.clientY - offsetY);
    };

    const onMouseUp = () => {
      window.removeEventListener('mousemove', onMouseMove);
      window.removeEventListener('mouseup', onMouseUp);
    };

    window.addEventListener('mousemove', onMouseMove);
    window.addEventListener('mouseup', onMouseUp);
  };

  const magnetStyle = {
    left: `${x}px`,
    top: `${y}px`,
    position: 'absolute',
    width: '70px',
    height: '70px',
    backgroundColor: type === 'north' ? '#ff2d55' : '#007aff',
    borderRadius: '50%',
    cursor: 'grab',
    boxShadow: '0 6px 15px rgba(0, 0, 0, 0.1)',
    transition: 'all 0.2s ease-in-out',
    zIndex: 10,
    transform: 'scale(1)',
    ':active': {
      transform: 'scale(0.9)',
    },
  };

  return (
    <div
      ref={magnetRef}
      className={`magnet ${type}`}
      style={magnetStyle}
      onMouseDown={handleMouseDown}
    />
  );
};

const App = () => {
  const [magnets, setMagnets] = useState([
    { id: 1, x: 100, y: 100, type: 'north' },
    { id: 2, x: 300, y: 200, type: 'south' },
  ]);

  const updateMagnetPosition = (id, x, y) => {
    setMagnets((prevMagnets) =>
      prevMagnets.map((magnet) =>
        magnet.id === id ? { ...magnet, x, y } : magnet
      )
    );
  };

  const checkInteraction = () => {
    // Add interactions between magnets, e.g., attraction/repulsion.
    setMagnets((prevMagnets) => {
      return prevMagnets.map((magnet) => {
        return {
          ...magnet,
          // Apply interaction logic here
        };
      });
    });
  };

  useEffect(() => {
    checkInteraction();
  }, [magnets]);

  const appStyle = {
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    height: '100vh',
    backgroundColor: '#f2f2f2',
    flexDirection: 'column',
    fontFamily: "'Roboto', sans-serif",
    padding: '20px',
  };

  const headerStyle = {
    fontSize: '2.5rem',
    fontWeight: '700',
    color: '#333',
    textShadow: '2px 2px 8px rgba(0, 0, 0, 0.1)',
    marginBottom: '20px',
    letterSpacing: '0.5px',
    textTransform: 'uppercase',
  };

  const containerStyle = {
    position: 'relative',
    width: '80%',
    height: '70%',
    backgroundColor: '#fff',
    borderRadius: '12px',
    boxShadow: '0 8px 20px rgba(0, 0, 0, 0.1)',
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    border: 'none',
    transition: 'all 0.3s ease',
    overflow: 'hidden',
    transform: 'scale(1)',
  };

  const infoTextStyle = {
    fontSize: '1.2rem',
    color: '#555',
    marginTop: '20px',
    textAlign: 'center',
    fontWeight: '600',
    letterSpacing: '1px',
  };

  return (
    <div style={appStyle}>
      <h1 style={headerStyle}>Magnet Simulation</h1>
      <div style={containerStyle}>
        {magnets.map((magnet) => (
          <Magnet
            key={magnet.id}
            x={magnet.x}
            y={magnet.y}
            type={magnet.type}
            onDrag={(newX, newY) => updateMagnetPosition(magnet.id, newX, newY)}
          />
        ))}
      </div>
      <div style={infoTextStyle}>
        <p>Drag the magnets around to see how they interact.</p>
        <p>The red magnet is North, and the blue one is South.</p>
      </div>
    </div>
  );
};

export default App;
