import React, { useState, useEffect, useRef } from 'react';

const Magnet = ({ x, y, type, onDrag }) => {
  const magnetRef = useRef(null);

  const handleMouseDown = (e) => {
    e.preventDefault();
    const offsetX = e.clientX - x;
    const offsetY = e.clientY - y;

    const onMouseMove = (e) => {
      onDrag(e.clientX - offsetX, e.clientY - offsetY);
    };

    const onMouseUp = () => {
      window.removeEventListener('mousemove', onMouseMove);
      window.removeEventListener('mouseup', onMouseUp);
    };

    window.addEventListener('mousemove', onMouseMove);
    window.addEventListener('mouseup', onMouseUp);
  };

  const magnetStyle = {
    left: `${x}px`,
    top: `${y}px`,
    position: 'absolute',
    width: '50px',
    height: '50px',
    backgroundColor: type === 'north' ? 'red' : 'blue',
    borderRadius: '50%',
    cursor: 'pointer',
    transition: 'all 0.1s ease-in-out',
  };

  return (
    <div
      ref={magnetRef}
      className={`magnet ${type}`}
      style={magnetStyle}
      onMouseDown={handleMouseDown}
    />
  );
};

const App = () => {
  const [magnets, setMagnets] = useState([
    { id: 1, x: 100, y: 100, type: 'north' },
    { id: 2, x: 300, y: 200, type: 'south' },
  ]);

  const updateMagnetPosition = (id, x, y) => {
    setMagnets((prevMagnets) =>
      prevMagnets.map((magnet) =>
        magnet.id === id ? { ...magnet, x, y } : magnet
      )
    );
  };

  const checkInteraction = () => {
    // Check interaction between magnets, e.g., attraction/repulsion
    setMagnets((prevMagnets) => {
      return prevMagnets.map((magnet) => {
        return {
          ...magnet,
          // Apply simple distance-based interaction logic here
        };
      });
    });
  };

  useEffect(() => {
    checkInteraction();
  }, [magnets]);

  const appStyle = {
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    height: '100vh',
    backgroundColor: '#f0f0f0',
    flexDirection: 'column',
    textAlign: 'center',
  };

  return (
    <div style={appStyle}>
      <h1>Magnet Simulation</h1>
      {magnets.map((magnet) => (
        <Magnet
          key={magnet.id}
          x={magnet.x}
          y={magnet.y}
          type={magnet.type}
          onDrag={(newX, newY) => updateMagnetPosition(magnet.id, newX, newY)}
        />
      ))}
    </div>
  );
};

export default App;
